<script>
    var alerta = @json($alerta);
    var competencia = @json($competencia ?? null);

    
    $('#TabAlertaOcorrencias').DataTable({
        language: {
            url: 'https://cdn.datatables.net/plug-ins/2.1.7/i18n/pt-BR.json'
        },
        layout: {
            topStart: {
                pageLength: {
                    menu: [ 10, 25, 50, 100 ]
                },
                buttons: [
                    {
                        extend: 'pdfHtml5',
                        orientation: 'landscape',
                        pageSize: 'A4',
                        customize: function (doc) {
                            var logoada = 'data:image/png;base64,';
                            doc.content.splice(0, 1, {
                                    text: null,
                                });
                                doc.content.splice(0, 0, {
                                    image: logoada,
                                    width: 250, 
                                    alignment: 'center' 
                                });
                                doc.content.splice(1, 0, {
                                    text: alertaText,
                                    fontSize: 16,
                                    bold: true,
                                    alignment: 'center',
                                    margin: [0, 10, 0, 10]
                                });
                                doc.content.splice(2, 0, {
                                    text: compText,
                                    fontSize: 16,
                                    bold: true,
                                    alignment: 'center',
                                    margin: [0, 10, 0, 10]
                                });
                        }
                    },
                    {
                        extend: 'copyHtml5'
                    },
                    {
                        extend: 'excelHtml5'
                    }
                ]
            }
        }
    });

    // Evento para capturar os dados da linha onde o botão foi clicado
    $(document).on('click', '.btn-acompanhamento', function () {
        // Encontra a linha correspondente ao botão clicado
        var row = $(this).closest('tr');
        
        // Captura os dados da linha
        var dados = {
            ocorrencia: row.find('td:eq(0)').text(),
            unidade: row.find('td:eq(1)').text(),
            equipe: row.find('td:eq(2)').text(),
            competencia: row.find('td:eq(3)').text()
        };

        // Cria a URL com os parâmetros
        var rota = "{{ route('alertasAcompanhamento') }}";
        var urlComParametros = rota + "?ocorrencia=" + encodeURIComponent(dados.ocorrencia) +
                               "&unidade=" + encodeURIComponent(dados.unidade) +
                               "&equipe=" + encodeURIComponent(dados.equipe) +
                               "&competencia=" + encodeURIComponent(dados.competencia);

        // Redireciona para a URL com os parâmetros
        window.open(urlComParametros, '_blank');
    });
</script>
